import React from "react";
import { Link } from "react-router-dom";
import { slide as Menu } from "react-burger-menu";
import '../CSS/Sidebar.css'
import Button from './Button';


class Ham extends React.Component {
  //create a state!!!!!!! in the state: 
  constructor(props) {
    super(props)
    this.state = {
      menuOpen: false
    }
  }

  closeMenu() {
    this.setState({ menuOpen: false })
  }


  return() {
    <Menu className='menu'>
      <Link onClick={() => this.closeMenu()} className='menu-item' to="/">Home</Link>

      <Link onClick={() => this.closeMenu()} className="menu-item" to="/projects">Projects</Link>

      <Link onClick={() => this.closeMenu()} className="menu-item" to="/about">
        About
      </Link>

      <Link onClick={() => this.closeMenu()} className="menu-item" to="/contact">
        Contact
      </Link>
      <Link onClick={() => this.closeMenu()} className="menu-item" to="/days-of-code">
        Days of Code
      </Link>
      <Button handleSwitch={props.handleSwitch} switched={true} />
      <label className="switch" >
        <input type="checkbox" onChange={props.handleSwitch} checked={true} />
        <span className="slider round"></span>
      </label>
    </Menu>
  }
}

export default Ham;
